mapper.select:
select *			 
from mcboard 
where title like #{title}

mapper.insert:
insert into mcboard (
	boardnum
	, id
	, title
	, content
	, startDate
	, endDate
	, startPlace
	, tripArea
	, male
	, female
	
	, originalfile
	, savedfile
	
)
values (
	mcboard_seq.nextval
	, #{id}
	, #{title}
	, #{content}
	, #{startDate}
	, #{endDate}
	, #{startPlace}
	, #{tripArea}
	, #{male}
	, #{female}
	
	, #{originalfile}
	, #{savedfile}
	
)

mapper.update:
update mcboard set 
	title = #{title}
	, content = #{content}
	
	, originalfile = #{originalfile}
	, savedfile = #{savedfile}
	
where 
	boardnum = #{boardnum} and id = #{id}
