mapper.insert:
insert into tbl_goods
(
gdsNum
, gdsName
, cateCode
, gdsPrice
, gdsStock
, gdsDes
, userId


			, gdsOriginalfile
			, gdsSavedfile

)
values
(
tbl_goods_seq.nextval
, #{gdsName}
, #{cateCode}
, #{gdsPrice}
, #{gdsStock}
, #{gdsDes}
, #{userId}



			, #{gdsOriginalfile}
			, #{gdsSavedfile}

)

mapper.delete:
delete tbl_cart
    where cartNum = #{cartNum}
        and userId = #{userId}

mapper.select:
select*from tbl_reply
where gdsNum = #{gdsNum}
order by gdsNum desc

	select g.gdsNum, g.cateCode, g.gdsName, g.gdsSavedfile, (select count(*) from tbl_reply r where g.gdsNum = r.gdsNum)as replyCount
	from tbl_goods g
	where userId = #{userId}
